.dx-field-label {
  color: #333;
  cursor: default;
  padding: 5px 8px 5px 0;
}
.dx-field-value.dx-attention {
  color: #d9534f;
  padding: 5px 6px 5px 22px;
  position: relative;
}
.dx-field-value.dx-attention:before {
  pointer-events: none;
  font-weight: 700;
  position: absolute;
  top: 50%;
  margin-top: -8px;
  width: 16px;
  height: 16px;
  text-align: center;
  line-height: 16px;
  font-size: 11px;
  background-color: #d9534f;
  color: #fff;
  content: '!';
  border-radius: 50%;
  left: 0;
}
.dx-field-value-static,
.dx-field-value:not(.dx-switch):not(.dx-checkbox):not(.dx-button) {
  width: 60%;
}
